* {
  margin: 0;
  padding: 0;
  box-sizing: border-box; }

html, body {
  width: 100%;
  height: 100%; }

body {
  background: url("e964c7c77287c9db1a68d77e36f728c5.png") 0 0 no-repeat fixed;
  background-size: 100% 100%;
  text-align: center;
  font-size: 14px; }

ul {
  list-style: none; }

/*=============== header ================*/
header {
  padding: 30px 0;
  font-size: 0; }
  header > img {
    width: 68px;
    height: 68px;
    border-radius: 50%;
    display: inline-block; }
  header > ul {
    margin-top: 20px; }
    header > ul > li {
      font-size: 18px;
      color: #fff;
      font-weight: 700;
      padding: 0 10px;
      display: inline-block;
      line-height: 30px;
      position: relative; }
      header > ul > li:not(:last-child):after {
        content: '';
        width: 1px;
        height: 18px;
        background-color: #fff;
        position: absolute;
        right: 0;
        top: 5px; }

/* =================== section ========================= */
section {
  min-height: 300px;
  background-color: #fff;
  margin: 0 6px;
  border-radius: 20px;
  padding: 15px 0 8px; }
  section > .tab {
    border: 1px solid #de1f26;
    margin: 0 15px;
    overflow: hidden;
    border-radius: 35px; }
    section > .tab > li {
      color: #666;
      width: 50%;
      float: left;
      line-height: 40px;
      font-size: 16px; }
      section > .tab > li.active {
        color: #fff;
        background-color: #de1f26; }
  section > .panel {
    padding: 15px; }
    section > .panel > .panel-popular {
      display: none; }
    section > .panel li {
      text-align: left;
      display: flex;
      align-items: center;
      padding: 8px 0; }
      section > .panel li > span {
        color: #666; }
        section > .panel li > span:not(:last-child) {
          margin-right: 18px; }
        section > .panel li > span.id {
          font-weight: 700;
          font-size: 18px;
          color: #333;
          width: 18px; }
        section > .panel li > span.name {
          flex: 1; }
        section > .panel li > span:last-child {
          padding-left: 14px;
          background: url("9af379d4deb5b114948315be73b2638b.png") no-repeat left center;
          background-size: 10px; }
      section > .panel li > img {
        width: 40px;
        height: 40px;
        border-radius: 50%;
        margin-right: 18px; }

/* ================== footer ===================== */
footer {
  color: #f0f0f0;
  margin: 35px 0; }
